mapper.select:
select c.course_id as courseId, student_id as studentId, mark
from course c inner join selectedcourse s on c.course_id = s.course_id
where student_id = #{studentId}

select course_id as courseId, student_id as studentId, mark
from selectedcourse
where course_id = #{courseId}

mapper.update:
update selectedcourse
set mark = #{mark}
where student_id = #{studentId} and course_id = #{courseId}

mapper.select:
select exists(
    select 1 from selectedcourse
    where student_id = #{studentId} and course_id = #{courseId}
)
